// page init

jQuery(function(){
	initSlideShow();
	jcf.replaceAll();
	shedule();
	mobileMenu();
	count();
	searchToggle();
	modalPopup();
	scrollFix();
	$( ".datepicker" ).datepicker();
	validateTime();
});

function validateTime(){
	$('.js-check-hours').each(function () {
		$(this).on('blur', function () {
			var val = $(this).val();
			if (isNaN(val)){
				$(this).val('');
			}
			else if (val > 24){
				$(this).val('24');
			}
			else if (val < 0){
				$(this).val('0');
			}
		});
	});

	$('.js-check-minutes').each(function () {
		$(this).on('blur', function () {
			var val = $(this).val();
			if (isNaN(val)){
				$(this).val('');
			}
			else if (val > 59){
				$(this).val('59');
			}
			else if (val < 0){
				$(this).val('0');
			}
		});
	});
}

function modalPopup(){
	$('[data-modal]').on('click', function (e) {
		e.preventDefault();
		var targetPopup = $(this).data('modal');

		$(targetPopup).addClass('active');
		$('body').addClass('scroll-lock');
	});

	$('.modal-block .close').on('click', function (e) {
		e.preventDefault();
		$(this).closest('.modal-block').removeClass('active');
		$('body').removeClass('scroll-lock');
	});
}

function scrollFix(){
	$('.modal-block').on('scroll', function () {
		$('.jcf-select-drop').remove();
		$('.jcf-select.jcf-drop-active').removeClass('jcf-drop-active');
	})
}

function count() {
	var holder = $('.action'),
	valHolder = holder.find('.val'),
	valBtn = holder.find('.btn-val'),
	valPrice = holder.find('.price-val'),
	inc = holder.find('.inc'),
	dec = holder.find('.dec'),
	price = holder.data('price');

	inc.on('click', function (e) {
		e.preventDefault();
		val = valHolder.text();
		val++;
		(val > 99) ? val = 99 : '';

		valHolder.text(val);
		valBtn.text(val);
		valPrice.text(val*price);
	});

	dec.on('click', function (e) {
		e.preventDefault();
		val = valHolder.text();
		val--;
		(val < 1) ? val = 1 : '';

		valHolder.text(val);
		valBtn.text(val);
		valPrice.text(val*price);
	});
}

function mobileMenu() {
	var holder = $('body'),
	opener = $('.menu-opener'),
	slide = $('.menu-slide');

	opener.on('click', function () {
		holder.toggleClass('menu-active');
	});
}

function searchToggle() {
	var holder = $('.search-block'),
	opener = $('.search-opener'),
	slide = $('.search-slide');

	opener.on('click', function () {
		holder.toggleClass('search-active');
	});
	$(document).on('click', function(e){
		if (!holder.is(e.target) && holder.has(e.target).length === 0) {
			holder.removeClass('search-active');
		}
	});
}

function shedule() {
	var holder = $('.shedule'),
	items = $('.item'),
	openers = holder.find('.item');

	openers.on('click', function () {
		items.removeClass('active');
		$(this).closest('.item').addClass('active');
	});
}

// slideshow init
function initSlideShow() {
	jQuery('.promo').fadeGallery({
		useSwipe: true,
		slides: '.slide',
		switchSimultaneously: true,
		disableWhileAnimating: true,
		autoRotation: true,
		pauseOnHover: false,
		autoHeight: true,
		switchTime: 4000,
		animSpeed: 500,
		pagerLinks: '.switcher a'
	});
}

